import { Card } from "react-bootstrap";
import { useState } from "react";
import "../feedback.css";
import { useNavigate } from "react-router-dom";

function FeedBack({ selectFeebackRate }) {
  const [number, selectNumber] = useState(null);
  const navigate = useNavigate();
  const OnSelectNumber = (number) => {
    selectNumber(number);
  };

  const OnSubmit = () => {
    selectFeebackRate(number);
    navigate("/thankYou");
  };

  return (
    <>
      <div className="d-flex justify-content-center pt-5 ">
        <Card className="feedback">
          <Card.Body>
            <div style={{ paddingLeft: "10px" }}>
              <button className="circular-button">
                <img alt="star" src="icon-star.svg" className="star-img" />
              </button>
            </div>
            <Card.Title className="pt-4 card-title" style={{ paddingLeft: "10px" }}>
              How did we do?
            </Card.Title>
            <Card.Text className="card-text">
              Please let us know how we did with your support request. All feedback is appreciated to help us improve
              our offering!
            </Card.Text>  
            <div className="d-flex justify-content-around mb-3">
              <button type="button" className={`circular-button ${number === 1 ? "button-selected" : ""}`} onClick={() => OnSelectNumber(1)}>
                1
              </button>
              <button type="button" className={`circular-button ${number === 2 ? "button-selected" : ""}`} onClick={() => OnSelectNumber(2)}>
                2
              </button>
              <button type="button" className={`circular-button ${number === 3 ? "button-selected" : ""}`} onClick={() => OnSelectNumber(3)}>
                3
              </button>
              <button type="button" className={`circular-button ${number === 4 ? "button-selected" : ""}`} onClick={() => OnSelectNumber(4)}>
                4
              </button>
              <button type="button" className={`circular-button ${number === 5 ? "button-selected" : ""}`} onClick={() => OnSelectNumber(5)}>
                5
              </button>
            </div>
            <div className="d-flex justify-content-center">
              <button className="rounded-pill d-felx justify-content-center mt-2 submit-button" onClick={OnSubmit}>
                SUBMIT
              </button>
            </div>
          </Card.Body>
        </Card>
      </div>
    </>
  );
}

export default FeedBack;
