import { Card } from "react-bootstrap";
import "../circulaireButton.css";
import "../starIcon.css";
import "../feedback.css";
import "../cardText.css"; 
import "../submitButton.css";
import { useState } from "react";
function FeedBack() {
  const [rate,selectRate]=useState(null); 
  const [number,selectNumber]=useState(null)
  const OnSelectNumber=(number)=>{ 
      selectNumber(number)
  }
  const OnSelectFeedback=()=>{ 
    selectRate(number)
  }
  return (
    <>
      <div className="d-flex justify-content-center pt-5 ">
        <Card
          className="feedback"
        >
          <Card.Body>
            <div style={{paddingLeft:"10px"}}>
              <button className="circular-button ">
                <img alt="star" src="icon-star.svg" class="star-img"  />
              </button>
            </div>
            <Card.Title
              className="pt-4 card-title"
              style={{paddingLeft:"10px"}}
            >
              How did we do?
            </Card.Title>
            <Card.Text
             className="card-text"
            >
              Please let us know how we did with your support request. All
              feedback is appreciated to help us improve our offering!
            </Card.Text>
            <div className="d-flex justify-content-around mb-3">
              <button type="button" className="circular-button" onClick={OnSelectNumber}>
                1
              </button>
              <button type="button" className="circular-button" onClick={OnSelectNumber}>
                2
              </button>
              <button type="button" className="circular-button">onClick={OnSelectNumber}
                3
              </button>
              <button type="button" className="circular-button">
                4
              </button>
              <button type="button" className="circular-button">
                5
              </button>
            </div>
            <div className="d-flex justify-content-center">
              <button
                className="rounded-pill d-felx justify-content-center mt-2 submit-button"
                onClick={OnSelectFeedback}
              >
                SUBMIT
              </button>
            </div>
          </Card.Body>
        </Card>
      </div>
    </>
  );
}

export default FeedBack;
