import { Card } from "react-bootstrap";
import { useState } from "react";
import "../feedback.css";
import { useNavigate } from "react-router-dom";
const buttons = [1, 2, 3, 4, 5];
function FeedBack({ selectFeebackRate }) {
  const [number, selectNumber] = useState(null);
  const navigate = useNavigate();
  const OnSelectNumber = (number) => {
    selectNumber(number);
  };

  const OnSubmit = () => {
    selectFeebackRate(number);
    navigate("/thankYou");
  };

  return (
    <>
      <div className="d-flex justify-content-center pt-5 ">
        <Card className="feedback">
          <Card.Body>
            <div className="star-img" style={{ paddingLeft: "10px" }}>
              <button className="circular-button">
                <img alt="star" src="icon-star.svg"  />
              </button>
            </div>
            <Card.Title className="pt-4 card-title" style={{ paddingLeft: "10px" }}>
              How did we do?
            </Card.Title>
            <Card.Text className="card-text">
              Please let us know how we did with your support request. All feedback is appreciated to help us improve
              our offering!
            </Card.Text>  
            <div className="d-flex justify-content-around mb-3">
            {buttons.map((item) => (
    <button
      type="button"
      className={`circular-button ${number === item ? "button-selected" : ""}`}
      style={{
        backgroundColor: number === item ? "hsl(25, 97%, 53%)" : "#3e4750",
        color: number === item ? "white" : "hsl(216, 12%, 54%)",
      }}
      onClick={() => OnSelectNumber(item)}
      key={item}
    >
      {item}
    </button>
  ))}
            </div>
            <div className="d-flex justify-content-center">
              <button className="rounded-pill d-felx justify-content-center mt-2 submit-button" onClick={OnSubmit}>
                SUBMIT
              </button>
            </div>
          </Card.Body>
        </Card>
      </div>
    </>
  );
}

export default FeedBack;
